mapper.select:
select i.item_json ->> 'event_name' as event, d.dev_title as device, count(*) from data.itemEventJson as i join data.devinfo as d on d.dev_ch = (i.item_json ->> 'dev_channel')::integer where to_date(i.item_json ->> 'event_time', 'YYYY-MM-DD') = to_date(#{time}, 'YYYY-MM-DD')
and i.item_json ->> 'event_name' in
#{event}
and d.dev_title in
#{device}
group by to_date(i.item_json ->> 'event_time', 'YYYY-MM-DD'), d.dev_title, i.item_json ->> 'event_name' order by dev_title asc

select *
from data.eventCount
order by event_time desc;

SELECT d.dev_title as title, ch, time, ARRAY_TO_STRING(ARRAY_AGG(event),',') as event FROM ( select i.ch, i.time, i.event from data.categoryAll as c left join ( select a.ch, a.time, a.event_name || '_' || count(*) as event from ( select dev_ch::integer as ch, substring(event_time, 1, #{type}) as time, lower(split_part(regexp_split_to_table(substring(tags, 2, length(tags) - 2), ', '),'_',2)) as event_name from data.imageevent where dev_ch::integer in
#{ch}
and
to_timestamp(event_time, 'YYYY-MM-DD')::timestamp between #{startDate}::timestamp and #{startDate}::timestamp
to_timestamp(event_time, 'YYYY-MM-DD')::timestamp between #{startDate}::timestamp and #{endDate}::timestamp
group by ch, substring(event_time, 1, #{type}), lower(split_part(regexp_split_to_table(substring(tags, 2, length(tags) - 2), ', '),'_',2)), event_time order by ch asc, time asc, event_name asc ) a where event_name = #{eventName} group by a.ch, a.time, a.event_name order by ch asc, time asc, event asc ) as i on lower(split_part(c.category_small, '_', 2)) = split_part(i.event, '_', 1) where i.time is not null order by i.ch asc, i.time asc, c.category_idx asc ) a left join data.devinfo as d on d.dev_ch = ch GROUP BY title, ch, time;

select dev_ch as ch, dev_title as title
from data.devinfo
order by dev_ch asc;
