from data.devinfo
where dev_title = #{dev_title};

select a.*, d.*
from 
	data.devinfo as d
	join
		data.actioninfo as a
		on a.dev_title = d.dev_title


		where lower(a.action_event) = lower(#{model_name}) and (POSITION(#{action_action} in a.action_action) > 0) and a.dev_title = #{dev_title} 
		and a.action_source = #{action_source};


		where lower(a.action_event) = lower(#{model_name}) and a.action_action = #{action_action} and a.dev_title = #{dev_title} 
		and a.action_source = #{action_source};

select *
from data.devinfo
where dev_ip = #{ip}

	and dev_web_port = #{port}

order by dev_idx desc
limit 1;

